DROP SCHEMA IF EXISTS project1 CASCADE;

CREATE SCHEMA IF NOT EXISTS project1;

GRANT ALL PRIVILEGES ON DATABASE pg_db TO PUBLIC;
GRANT USAGE, SELECT ON ALL SEQUENCES IN SCHEMA project1 TO PUBLIC;
GRANT ALL PRIVILEGES ON ALL TABLES IN SCHEMA project1 TO PUBLIC;

SET search_path = project1;

CREATE TABLE IF NOT EXISTS users
(
    id         SERIAL PRIMARY KEY,
    username   VARCHAR(100) NOT NULL UNIQUE,
    password   VARCHAR(100) NOT NULL,
    base64Icon TEXT         NOT NULL,
    email      VARCHAR(255) UNIQUE
);

CREATE TABLE IF NOT EXISTS comments
(
    id          SERIAL PRIMARY KEY,
    userId      INT,
    textMessage VARCHAR(320) NOT NULL,
    createAt    TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updateAt    TIMESTAMP,
    rateCount   INT       DEFAULT 0,
    isEdit      BOOLEAN   DEFAULT FALSE,
    parentId    INT,
    CONSTRAINT parent_child FOREIGN KEY (parentId) REFERENCES comments (id) ON DELETE CASCADE,
    CONSTRAINT fk_user FOREIGN KEY (userId) REFERENCES users (id)
);




